mapper.insert:
insert 
  into tb_alarm
  (
  	al_no
  , al_target_mno
  , al_category
  , al_board_no
  , al_content
  )
  values
  (
  	seq_alno.nextval
  , #{boardWriter}
  , #{cmd}
  , #{boardNo}
  , #{alarmContent}
  )

mapper.select:
select reply_wno
  from tb_reply r
 where reply_no = ${replyParentNo}
