mapper.select:
select count(*)
  from tb_member
 where dept_code != 'D0'

	and job_code = #{jobCode}
	and dept_code = #{deptCode}


	and job_code = #{jobCode}


	and dept_code = #{deptCode}



 		and mem_resign_status = 'N'
 		and mem_name like '%'||#{keyword}||'%'


 		and mem_resign_status = 'Y'
 		and mem_name like '%'||#{keyword}||'%'


 		and mem_name like '%'||#{keyword}||'%'

select mem_no , mem_id , mem_pwd , mem_name , dept_name , job_name , mem_phone , mem_businessnum , mem_email , mem_birth , mem_postal_code , mem_postal_ad , mem_postal_detail , mem_postal_refer , mem_enroll_date , mem_resign_date , mem_resign_status , mem_profile from tb_member t join tb_department d on (d.dept_code = t.dept_code) join tb_job j on (j.job_code = t.job_code) where t.dept_code != 'D0'
and t.job_code = #{jobCode} and t.dept_code = #{deptCode}
and t.job_code = #{jobCode}
and t.dept_code = #{deptCode}
and mem_resign_status = 'N' and mem_name like '%'||#{keyword}||'%'
and mem_resign_status = 'Y' and mem_name like '%'||#{keyword}||'%'
and mem_name like '%'||#{keyword}||'%'
order by mem_no desc

