mapper.select:
SELECT COUNT(*) FROM( SELECT * FROM( SELECT A.ATT_NO , C.MEM_NO , M.MEM_NAME , NVL(ATT_DATE, TO_CHAR(WORKING_DAY, 'YYYY-MM-DD')) att_date , ATT_FIXEDIN , ATT_FIXEDOUT , ATT_IN , ATT_OUT , ATT_WORKOFF , ATT_WORKTIME , NVL(ATT_STATUS, 'F') att_status , ATT_HSTATUS FROM TB_ATTENDANCE A right JOIN ( SELECT WORKING_DAY, MEM_NO FROM ( SELECT TO_DATE(#{startDate}) + LEVEL - 1 working_day FROM DUAL CONNECT BY LEVEL <= 1000 ), (SELECT * FROM TB_MEMBER) ) C ON (ATT_DATE = WORKING_DAY AND A.MEM_NO = C.MEM_NO) JOIN TB_MEMBER M ON (C.MEM_NO=M.MEM_NO) WHERE WORKING_DAY <= TO_DATE(#{endDate})
AND DEPT_CODE = #{deptCode}
AND JOB_CODE = #{jobCode}
AND MEM_NAME LIKE '%'||#{keyword}||'%' ORDER BY 4 DESC, 2) WHERE
(ATT_STATUS = 'D' OR ATT_STATUS = 'H')
(ATT_STATUS = 'L' OR ATT_STATUS = 'E')
(ATT_STATUS = 'F')
(ATT_STATUS = 'D' OR ATT_STATUS = 'H' OR ATT_STATUS = 'L' OR ATT_STATUS = 'E')
(ATT_STATUS = 'D' OR ATT_STATUS = 'H' OR ATT_STATUS = 'F')
(ATT_STATUS = 'L' OR ATT_STATUS = 'E' OR ATT_STATUS = 'F')
1=1
AND ATT_DATE != #{date.reRestdate}
)

SELECT COUNT(*) FROM( SELECT * FROM( SELECT A.ATT_NO , C.MEM_NO , M.MEM_NAME , NVL(ATT_DATE, TO_CHAR(WORKING_DAY, 'YYYY-MM-DD')) att_date , ATT_FIXEDIN , ATT_FIXEDOUT , ATT_IN , ATT_OUT , ATT_WORKOFF , ATT_WORKTIME , NVL(ATT_STATUS, 'F') att_status , ATT_HSTATUS FROM TB_ATTENDANCE A right JOIN ( SELECT WORKING_DAY, MEM_NO FROM ( SELECT TO_DATE(#{startDate}) + LEVEL - 1 working_day FROM DUAL CONNECT BY LEVEL <= 1000 ), (SELECT * FROM TB_MEMBER) ) C ON (ATT_DATE = WORKING_DAY AND A.MEM_NO = C.MEM_NO) JOIN TB_MEMBER M ON (C.MEM_NO=M.MEM_NO) WHERE WORKING_DAY <= TO_DATE(#{endDate}) ORDER BY 4 DESC, 2) WHERE
(ATT_STATUS = 'D' OR ATT_STATUS = 'H')
(ATT_STATUS = 'L' OR ATT_STATUS = 'E')
(ATT_STATUS = 'F')
1=1
AND MEM_NO = #{userNo}
AND ATT_DATE != #{date.reRestdate}
)

SELECT * FROM( SELECT A.ATT_NO , C.MEM_NO , M.MEM_NAME , M.MEM_ENROLL_DATE , NVL(ATT_DATE, TO_CHAR(WORKING_DAY, 'YYYY-MM-DD')) att_date , ATT_FIXEDIN , ATT_FIXEDOUT , ATT_IN , ATT_OUT , ATT_WORKOFF , ATT_WORKTIME , NVL(ATT_STATUS, 'F') att_status , ATT_HSTATUS FROM TB_ATTENDANCE A right JOIN ( SELECT WORKING_DAY, MEM_NO FROM ( SELECT TO_DATE(#{startDate}) + LEVEL - 1 working_day FROM DUAL CONNECT BY LEVEL <= 1000 ), (SELECT * FROM TB_MEMBER) ) C ON (ATT_DATE = WORKING_DAY AND A.MEM_NO = C.MEM_NO) JOIN TB_MEMBER M ON (C.MEM_NO=M.MEM_NO) WHERE WORKING_DAY <= TO_DATE(#{endDate})
AND WORKING_DAY != #{date.reRestdate}
ORDER BY 4 DESC, 2) WHERE MEM_NO = #{userNo}

SELECT * FROM( SELECT A.ATT_NO , C.MEM_NO , M.MEM_NAME , M.MEM_ENROLL_DATE , NVL(ATT_DATE, TO_CHAR(WORKING_DAY, 'YYYY-MM-DD')) att_date , ATT_FIXEDIN , ATT_FIXEDOUT , ATT_IN , ATT_OUT , ATT_WORKOFF , ATT_WORKTIME , NVL(ATT_STATUS, 'F') att_status , ATT_HSTATUS , DY FROM TB_ATTENDANCE A right JOIN ( SELECT WORKING_DAY, MEM_NO, DY FROM ( SELECT TO_DATE(#{startDate}) + LEVEL - 1 working_day, TO_CHAR(TRUNC(TO_DATE(#{startDate}), 'MM') + LEVEL - 1, 'DY') "DY" FROM DUAL CONNECT BY LEVEL <= 1000 ), (SELECT * FROM TB_MEMBER) ) C ON (ATT_DATE = WORKING_DAY AND A.MEM_NO = C.MEM_NO) JOIN TB_MEMBER M ON (C.MEM_NO=M.MEM_NO) WHERE WORKING_DAY <= TO_DATE(#{endDate})
ORDER BY 4 DESC, 2) WHERE MEM_NO = #{userNo}
AND ATT_DATE != #{date.reRestdate}

