mapper.select:
select count(*)
  from tb_holiday
 where mem_no = #{memNo}
   and extract(YEAR from ho_date) = #{year}

select count(*) from TB_APRV_FORM af join TB_ANNUAL_LEAVE al on (af.aprv_no = al.aprv_no) join tb_member m on (m.mem_no = af.mem_no) join tb_department d on (d.dept_code = m.dept_code) join tb_job j on (j.job_code = m.job_code) where APRV_STATUS = 'D'
and m.dept_code = #{deptCode}
and m.job_code = #{jobCode}
and to_date(APRV_DOC_ENDATE) between to_date(#{startDate}) and to_date(#{endDate})
and mem_name like '%'||#{keyword}||'%'

select af.mem_no, af.aprv_no, mem_name, dept_name, job_name, LEAVE_START_DATE, LEAVE_END_DATE, REQUEST_PERIOD, S_HALF_DAY_LEAVE, F_HALF_DAY_LEAVE, leave_type, leave_reason from TB_APRV_FORM af join TB_ANNUAL_LEAVE al on (af.aprv_no = al.aprv_no) join tb_member m on (m.mem_no = af.mem_no) join tb_department d on (d.dept_code = m.dept_code) join tb_job j on (j.job_code = m.job_code) where APRV_STATUS = 'D'
and m.dept_code = #{deptCode}
and m.job_code = #{jobCode}
and to_date(APRV_DOC_ENDATE) between to_date(#{startDate}) and to_date(#{endDate})
and mem_name like '%'||#{keyword}||'%' order by 4 desc

select count(*) from tb_holiday h join tb_member m on (m.mem_no = h.mem_no) join tb_department d on (d.dept_code = m.dept_code) join tb_job j on (j.job_code = m.job_code) where mem_name like '%'||#{keyword}||'%'
and m.dept_code = #{deptCode}
and m.job_code = #{jobCode}
and ho_date between to_date(#{startDate}) and to_date(#{endDate})

select h.mem_no mem_name, dept_name, job_name, to_char(ho_date,'YYYY-MM-DD') ho_date, ho_num, ho_comment from tb_holiday h join tb_member m on (m.mem_no = h.mem_no) join tb_department d on (d.dept_code = m.dept_code) join tb_job j on (j.job_code = m.job_code) where mem_name like '%'||#{keyword}||'%'
and m.dept_code = #{deptCode}
and m.job_code = #{jobCode}
and to_date(ho_date) between to_date(#{startDate}) and to_date(#{endDate})
order by 4 desc

